// FlipCard.js
import React, { useState } from "react";
import { motion, useMotionValue, useSpring } from "framer-motion";

function FlipCard({ front, back, depth = 5 }) {
  // Track whether the card is flipped
  const [isFlipped, setIsFlipped] = useState(false);

  // Handle card flip
  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div 
      className="w-full h-full relative perspective-1000 cursor-pointer"
      onClick={handleFlip}
      style={{ 
        perspective: 1200,
        transformStyle: "preserve-3d"
      }}
    >
      {/* The card container with 3D rotation */}
      <motion.div
        className="w-full h-full relative"
        style={{
          transformStyle: "preserve-3d",
        }}
        animate={{ 
          rotateY: isFlipped ? 180 : 0,
          z: isFlipped ? depth * 5 : 0, // Float up when flipped
        }}
        transition={{ 
          type: "spring", 
          damping: 15, 
          stiffness: 120, 
          mass: 1,
          duration: 0.6
        }}
      >
        {/* Front side */}
        <div
          className={`absolute w-full h-full rounded-3xl overflow-hidden depth-${depth}`}
          style={{
            transformStyle: "preserve-3d",
            backfaceVisibility: "hidden",
            WebkitBackfaceVisibility: "hidden",
            zIndex: isFlipped ? 0 : 1
          }}
        >
          {front}
        </div>

        {/* Back side */}
        <div
          className={`absolute w-full h-full rounded-3xl overflow-hidden depth-${depth}`}
          style={{
            transformStyle: "preserve-3d",
            backfaceVisibility: "hidden",
            WebkitBackfaceVisibility: "hidden",
            rotateY: 180,
            transform: "rotateY(180deg)",
            zIndex: isFlipped ? 1 : 0
          }}
        >
          {back}
        </div>
      </motion.div>
    </div>
  );
}

export default FlipCard;
